resourceRoot=getResourceRootElement(getThisResource() )localPlayer=getLocalPlayer()versionstring="MTA:DayZ\nwww.niceshot.eu"infoTable={}Login_Edit={}marwinButtons={}sx,sy=guiGetScreenSize()font={}scale=1if sx < 1152 then	scale=sx/1152endif sx < 1024 then	outputChatBox("Najlepsza rozdzielczosc servera to: 1024x768 lub większa,zmien ja!",255,0,0)endfont[0]=guiCreateFont("font.ttf",14*scale )font[1]=guiCreateFont("font.ttf",18*scale )--Buttonfunction nowy_przycisk(x,y,widht,height,text,bool,parent,info)	button=guiCreateStaticImage(x,y,widht,height,"images/button_standard.png",bool,parent or nil)	table.insert(marwinButtons,button)	guiBringToFront(button)	label=guiCreateLabel(0,0,1,1,text,bool,button)	guiBringToFront(label)	setElementData(label,"parent",button)	setElementData(button,"info",info)	guiSetFont(label,font[1])	guiLabelSetVerticalAlign (label,"center")	guiLabelSetHorizontalAlign (label,"center")	addEventHandler("onClientMouseEnter",label,markButton,false)	addEventHandler("onClientMouseLeave",label,unmarkButton,false)	return labelendfunction markButton ()	parent=getElementData(source,"parent")	guiStaticImageLoadImage (parent,"images/button_mouse.png")	setElementData(getLocalPlayer(),"clickedButton",parent)	playSound("button.mp3")endfunction unmarkButton (b,s)	parent=getElementData(source,"parent")	guiStaticImageLoadImage (parent,"images/button_standard.png")	setElementData(getLocalPlayer(),"clickedButton",false)endfunction stworz_panellogowania()	guiSetInputMode("no_binds_when_editing")	showCursor(true)	--Create Window	--Background	tlo=guiCreateStaticImage(0.2,0.25,0.6,0.5,"images/background_1.png",true )	tab_front=guiCreateStaticImage(0,0,1,0.075,"images/tab.png",true,tlo)	--Header Text	headline=guiCreateLabel(0,0.15,1,0.8,"NiceShot.eu",true,tab_front)	guiSetFont(headline,font[1])	guiLabelSetHorizontalAlign (headline,"center")	--Version	guestInfo=guiCreateLabel(0.025,0.1,0.325,0.3,versionstring,true,tlo)	guiSetFont(guestInfo,font[1])	guiLabelSetHorizontalAlign (guestInfo,"center")	guiLabelSetColor (guestInfo,50,135,255)		--Login	--login_box=guiCreateStaticImage(0.025,0.1,0.325,0.85,"images/box_background.png",true,tlo)	loginButton=nowy_przycisk(0.1,0.825,0.175,0.1,"Zaloguj",true,tlo,"login")	--Text	loginInfo=guiCreateLabel(0.025,0.46,0.325,0.175,"Logowanie",true,tlo)	guiSetFont(loginInfo,font[1])	guiLabelSetHorizontalAlign (loginInfo,"center")	guiLabelSetColor (loginInfo,50,135,255)	--Username	username=guiCreateLabel(0.025,0.55,0.325,0.04,"Nick",true,tlo)	guiSetFont(username,font[0])	guiLabelSetHorizontalAlign (username,"center")	--Edit Box	Login_Edit[1]=guiCreateEdit(0.1,0.6,0.175,0.055,"CrosRoad95",true,tlo)	--Password	password=guiCreateLabel(0.025,0.675,0.325,0.04,"Haslo",true,tlo)	guiSetFont(password,font[0])	guiLabelSetHorizontalAlign (password,"center")	loginIcon=guiCreateStaticImage(0.07,0.46,0.03,0.05,"images/login_icon.png",true,tlo)	--Edit Box	Login_Edit[2]=guiCreateEdit(0.1,0.725,0.175,0.055,"asdasd",true,tlo)	guiEditSetMasked(Login_Edit[2],true)	--Register	registerButton=nowy_przycisk(0.45,0.825,0.175,0.1,"Zarejestruj",true,tlo,"register")	--Text	registerInfo=guiCreateLabel(0.375,0.15,0.325,0.1,"Rejestracja!",true,tlo)	guiSetFont(registerInfo,font[1])	guiLabelSetHorizontalAlign (registerInfo,"center")	guiLabelSetColor (registerInfo,50,135,255)	--Username	username=guiCreateLabel(0.375,0.25,0.325,0.04,"Nick",true,tlo)	guiSetFont(username,font[0])	guiLabelSetHorizontalAlign (username,"center")	--Edit Box	Login_Edit[3]=guiCreateEdit(0.45,0.3,0.175,0.055,"",true,tlo)	--Password	password=guiCreateLabel(0.375,0.375,0.325,0.04,"Haslo",true,tlo)	guiSetFont(password,font[0])	guiLabelSetHorizontalAlign (password,"center")	loginIcon=guiCreateStaticImage(0.41,0.15,0.03,0.05,"images/signup_icon.png",true,tlo)	--Edit Box	Login_Edit[4]=guiCreateEdit(0.45,0.425,0.175,0.055,"",true,tlo)			guiEditSetMasked(Login_Edit[4],true)	--Password #2	password2=guiCreateLabel(0.375,0.5,0.325,0.04,"Powtorz haslo",true,tlo)	guiSetFont(password2,font[0])	guiLabelSetHorizontalAlign (password2,"center")	--Edit Box	Login_Edit[5]=guiCreateEdit(0.45,0.55,0.175,0.055,"",true,tlo)			guiEditSetMasked(Login_Edit[5],true)	newsH=guiCreateLabel(0.726,0.115,0.25,0.05,"Nowosci:",true,tlo)	guiSetFont(newsH,font[0])	guiLabelSetHorizontalAlign (newsH,"center")	guiLabelSetColor (newsH,50,135,255)	newsy={	{"",false},	{"",false},	{"",false},	{"",false},	}	newsbox={}	for i=1,4 do		newsbox[i]=guiCreateStaticImage(0.73,0.16+0.2*(i-1),0.244,0.2,"images/news.png",true,tlo)		guiSetAlpha(newsbox[i],0.8)		news1=guiCreateLabel(0.025,0.125,1,1,newsy[i][1],true,newsbox[i])		guiSetFont(news1,"default-bold-small")		news_box1_new=guiCreateStaticImage(0,0,0.15,0.1,"images/new.png",true,newsbox[i])		guiSetVisible(news_box1_new,newsy[i][2])	end	--others	news_box=guiCreateStaticImage(0.726,0.1,0.25,0.85,"images/box_background.png",true,tlo)	guiCreateStaticImage(0.79,0.114,0.028,0.045,"images/on.png",true,tlo)end--LOGINfunction clickPanelButton (button,state)	if button == "left" and state == "up" then	local element=getElementData(getLocalPlayer(),"clickedButton")	if element then 		local info=getElementData(element,"info")		if info and info == "login" then			local username=guiGetText(Login_Edit[1])			local password=guiGetText(Login_Edit[2])			if not (tostring(username) == "") and not (tostring(password) == "") then				if string.len(username)>4 and string.len(password)>4 and string.len(username)<25 and string.len(password)<25 then					triggerServerEvent("wyslij_zaloguj",resourceRoot,username,password)				else					outputChatBox("[BŁĄD]#FF9900 Login/hasło jest za krótkie/długi!",255,255,255,true)				end			else				outputChatBox("[BŁĄD]#FF9900 Podaj login i hasło!",255,255,255,true)			end		elseif info and info == "register" then  				local username=guiGetText(Login_Edit[3])				local pass1=guiGetText(Login_Edit[4])				local pass2=guiGetText(Login_Edit[5])								if username~="" or pass1~="" or pass2~="" then					if string.len(username)>4 and string.len(pass1)>4 and string.len(username)<25 and string.len(pass1)<25 then						if pass1 == pass2 then							triggerServerEvent("wyslij_zarejestruj",resourceRoot,username,pass1)						else							outputChatBox("[BLAD]#FF9900 Podane hasła nie są identyczne!",255,255,255,true)						end					else						outputChatBox("[BLAD]#FF9900 Podany login/hasło jest za krótkie/dlugi",255,255,255,true)					end				else					outputChatBox("[BLAD]#FF9900 Podaj login i hasło!",255,255,255,true)				end				end		end	endendaddEventHandler("onClientClick",getRootElement(),clickPanelButton)addEventHandler("onClientResourceStart",resourceRoot,	function ()		stworz_panellogowania()		guiSetVisible(tlo,false)		showLoginWindow(true)		guiSetInputMode("no_binds_when_editing")		--playSound("winsound.mp3")		fadeCamera (true) 		setCameraMatrix(-256.76953125,-426.6572265625,12.220329284668,-159.232421875,-332.634765625,11.737413406372)	end)function hideLoginWindow(accountName,pass)	showLoginWindow(false)endaddEvent("onPlayerDoneLogin",true)addEventHandler("onPlayerDoneLogin",getRootElement(),hideLoginWindow)function showLoginWindow(bool)setElementData(getLocalPlayer(),"clickedButton",false)	showCursor(bool)	if bool then		guiSetPosition(tlo,0.2,-0.75,true)		addEventHandler("onClientRender",getRootElement(),rollLoginPanel)		rollProgress=1		rollIn=true		guiSetInputMode("no_binds_when_editing")	else		guiSetPosition(tlo,0.2,0.25,true)		addEventHandler("onClientRender",getRootElement(),rollLoginPanel)		rollProgress=0		rollIn=false		guiSetInputMode("allow_binds")	end	randomDirAnim=(math.random() > 0.5) and -1 or 1	useXAxis=(math.random() > 0.5) and true or false	animType=useXAxis and "InBounce" or "InElastic"end--rollIn=truefunction rollLoginPanel ()	local eval	if rollIn then		if rollProgress > 0 then			rollProgress=((rollProgress * 1000) - 15)/1000			if rollProgress < 0 then rollProgress=0 end			eval=getEasingValue(rollProgress,animType)		else			removeEventHandler("onClientRender",getRootElement(),rollLoginPanel)			return		end	else		if rollProgress < 1 then			rollProgress=((rollProgress * 100) + 3)/100			if rollProgress > 1 then rollProgress=1 end			eval=getEasingValue(rollProgress,"InQuad")		else			removeEventHandler("onClientRender",getRootElement(),rollLoginPanel)			return		end	end	if useXAxis then		guiSetPosition(tlo,0.2,0.25+randomDirAnim*eval,true)		else		guiSetPosition(tlo,0.2+randomDirAnim*eval,0.25,true)	end	guiSetVisible(tlo,true)end